mapper.select:

SELECT
    bno, title, content, writer , regdate, updateDate
FROM
    (
    SELECT /*+INDEX_DESC(qna_board pk_board) */
        rownum rn, bno, title, content, writer , regdate, updateDate
    FROM qna_board
    where rownum <= #{pageNum} * #{amount}
    )
where rn > (#{pageNum} -1) * #{amount}
